const ticket = $json;
// Skip logic: Only process high/medium priority, technical tickets
const skip = !['high', 'medium'].includes(ticket.priority?.toLowerCase()) || ticket.type?.toLowerCase() !== 'technical';
return [{ skipAI: skip, ticket }];

const searchResults = $json;
const ticket = $items("AI Processing Needed?", 0)?.[0]?.json?.ticket;

// Extract relevant content from vector search results
let knowledge = '';
if (searchResults.documents && searchResults.documents.length > 0) {
  knowledge = searchResults.documents.map((doc, index) => {
    const meta = searchResults.metadatas?.[index] || {};
    return `### ${meta.title || 'Article'} (${meta.category || 'general'})
${doc}

`;
  }).join('');
}

return [{
  ticket,
  knowledge: knowledge.trim() || 'No relevant knowledge found.'
}];

